-- create doc_pages
-- _id: a unique string identifier for a particular page, in a particular PDF filename
-- filename: string of the filename of the PDF
-- title: a string, generated or manually entered title for the PDF
-- uuids: a set of strings that hold the weaviate UUIDs for the various fragments from the page number
create table doc_pages (_id string, filename string, title string, uuids stringset);

-- create doc_fragments
-- _id: the string UUID of the fragment in weaviate
-- filename: string of the filename of the PDF
-- title: a string, generated or manually entered title for the PDF
-- page_num: integer value of the page the fragment came from
-- fragment_num: integer value of the fragment number from the page (in order on the page)
-- prev_id: the string _id of the prior fragment's UUID which comes from weaviate
-- fragment: a string containing the content of the fragment
create table doc_fragments (_id string, filename string, title string, page_num int, page_id string, fragment_num int, prev_id string, fragment string);

-- create doc_questions
-- _id: the string UUID of the doc_fragment that was used to generate the question
-- filename: string of the filename of the PDF
-- title: a string, generated or manually entered title for the PDF
-- question: a string of the question posed by the LLM during the tandqs run, from a given text fragment
-- keyterms: a set of strings representing keyterms extracted from the text fragment
-- probability: a string to, in the future, store the probability of relevance of the question to the fragment (i.e. it could be a good or bad question)
create table doc_questions (_id string, filename string, title string, question string, keyterms stringset, page_id string, answer string, probability string);

-- create doc_keyterms
-- _id: a string for the keyterm extracted
-- filenames: string set of the filenames of the various PDFs
-- titles: string set, generated or manually entered title for the various PDFs
-- uuids: strings set of the various UUIDs of the fragments used to extract the keyterms
-- page_ids: string set containing the varous page_ids in doc_pages
create table doc_keyterms (_id string, filenames stringset, titles stringset, uuids stringset, page_ids stringset);

